package com.code.leet;

import java.util.*;

/*
You are given a string, s, and a list of words, words, that are all of the same length.
Find all starting indices of substring(s) in s that is a concatenation of each word in words
 exactly once and without any intervening characters.

For example, given:
s: "barfoothefoobarman"
words: ["foo", "bar"]

You should return the indices: [0,9].
(order does not matter).
 */
public class L0030SubstringwithConcatenationofAllWords {
    public List<Integer> findSubstring(String s, String[] words) {
        int[] tmp = new int[128];
        int len = 0;
        int length = s.length();
        int interval = words[0].length();

        List<Integer> r = new LinkedList <>();
        for(String word : words) {
            if (map.containsKey(word)) {
                Box box = map.get(word);
                box.val++;
                box.tmp++;
            } else {
                map.put(word, new Box(1,1));
            }
            for (int i = 0; i < word.length(); i++) {
                char c = word.charAt(i);
                tmp[c]++;
                len ++;
            }
        }

        if (len > length) {
            return r;
        }
        for (int i = 0; i < len; i++) {
            char c = s.charAt(i);
            tmp[c]--;
        }
        int left = 0;
        while(left + len <= length) {

            if (check(s, left, interval, len, words)) {
                    r.add(left);
            }

            if (left + len > length - 1) {
                break;
            }

            char c = s.charAt(left);
            tmp[c]++;
            char c1 = s.charAt(left + len);
            left++;
            tmp[c1]--;
        }

        return r;
    }
    HashMap<String, Box> map = new LinkedHashMap<>();
    class Box {
        int val;
        int tmp;
        Box(int val, int tmp) {
            this.val = val;
            this.tmp = tmp;
        }
    }
    private boolean check(String s, int left, int interval, int len, String[] words) {
        for (Box box : map.values()) {
            box.val = box.tmp;
        }

        int pos = 0;
        while (pos < len) {
            String str = s.substring(left + pos, left + pos + interval);
            if (!map.containsKey(str) || map.get(str).val <= 0) {
                return false;
            } else {
                Box box = map.get(str);
                box.val--;
            }
            pos += interval;
        }
        return true;
    }

    public static void main(String[] args) {
        String s = "";
//        String s = "barfoothefoobarman";
        String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
//        String[] words = {"foo","bar"};
        List <Integer> substring = new L0030SubstringwithConcatenationofAllWords().findSubstring(s, words);
        for (Integer i :
                substring) {
            System.out.println(i);
        }
    }
}
